namespace Hcode\Factory;


use Hcode\Model\Product;

class ProductFactory extends Factory
{

    public static function create($attributes = array()) : Product
    {
        return parent::create($attributes); // TODO: Change the autogenerated stub
    }

    public static function prepareListForCart($list){
        $preparetedList = parent::prepareList($list);
        foreach ($preparetedList as &$value){
            foreach ($list as $valueList){
                if($valueList['idproduct'] == $value['idproduct']){
                    $value['nrqtd'] = $valueList['nrqtd'];
                    $value['vltotal'] = $valueList['vltotal'];
                }
            }
        }

        return $preparetedList;
    }
}